// @flow
/*
 * provides constants to this project
 * @public
 */

export const DEFAULT_LIMIT = 10;
export const DEFAULT_SKIP = 0;
export const DEFAULT_SORT = { createdAt: 1 };
export const ASC = 'ASC';
export const DESC = 'DESC';
export const ENCODING = 'utf8';

export const SRC_DIR = 'src';
export const TEST_DIR = '__tests__';
export const TEST_GQL_DATA = 'input';
export const TEST_EXPECTED = 'output-expected';
export const TEST_GENERATED = 'output-generated';
export const TEST_GQL_EXTENSION = '.graphql';

export const INT = 'Int';
export const FLOAT = 'Float';
export const STRING = 'String';
export const ENUM = 'Enum';
export const BOOLEAN = 'Boolean';
export const BSON_TYPE = 'BSONType';
export const REGEX = 'Regex';
export const QUERY = 'Query';

// @flow
import { testCases } from './test-cases';
import { sendQueryAndExpect } from './sendQuery';
import { describe, before, it, Test } from 'mocha';
import { expect } from 'chai';

// dynamically define the test cases based on our test case registry
export const suite = describe('end-to-end test', () => {
  before(done => {
    testCases.forEach(testCase => {
      suite.addTest(
        new Test(`${testCase.name}`, () => {
          return sendQueryAndExpect(
            testCase.query,
            testCase.expectedResult,
            testCase.user
          );
        })
      );
    });

    done();
  });

  // start dummy test case, to get the whole thing running
  it('should start the test cases run', () => {
    expect(1).to.equal(1);
  });
});

// @flow

/**
 * CREATE-GRAPHQL-SERVER-QUERY-ARGUMENTS
 * 
 * this module adds query arguments to the schema
 * and maps the query arguments to a mongoDB data access
 *
 */

/* to find this path from various places, return this modules absolute path: */
export const modulePath = __dirname;

// prepares all the required queries for a mongoDB access
export { prepareQueries } from './model';

// provides a string with required types
// clones a given inputSchema and enhance it with query arguments
export { buildRequiredTypes, enhanceSchemaWithQueryArguments } from './schema';

// to provide the testCases to be used in create-graphql-server
export { testCases } from './test-cases';

// @flow
import cloneDeep from 'lodash.clonedeep';
import { merge } from 'lodash';
import { buildFilterQuery } from './buildFilterQuery';

/**
 * sets find(queryObject) with provided baseQuery plus additional filters
 * @public
 * @param {object} args - query arguments for the .find() method
 * @return {object} baseQuery - base query for the find method
 */

export function buildBaseQuery(args: any): any {
  let query = {};

  // if there is already a baseQuery provided from other model methods
  // apply the baseQuery to this query as a base
  if (args && args.baseQuery) {
    query = cloneDeep(args.baseQuery);
  }

  // if the user chose further filter criterias, build and merge them here
  if (args && args.filter) {
    query = merge(query, buildFilterQuery(args.filter));
  }

  // for pagination
  // default
  let lastCreatedAt = 0;

  // if there was sent a lastCreatedAt with the args, then use that instead
  if (
    args.lastCreatedAt &&
    (args.lastCreatedAt !== '' && typeof args.lastCreatedAt !== 'object')
  ) {
    lastCreatedAt = args.lastCreatedAt;
  }

  // check if the user entered a different filter criteria for "createdAt"
  // if so, take the one from the user, otherwise use the default for pagination
  if (!query['createdAt']) {
    query['createdAt'] = { $gt: lastCreatedAt };
  }

  return query;
}

// @flow
import isArrayLike from 'lodash.isarraylike';
import isObject from 'lodash.isobject';

/*
 * build a query for the type model accessing mongodb
 * @public
 */

export function buildFilterQuery(args: any): any {
  const query = {};

  // on primitive types, just return the data as leaf values in the recursion
  switch (typeof args) {
    case 'boolean':
      return args;
    case 'number':
      return args;
    case 'string':
      return args;
    case 'function':
      return args;
    case 'object':
      // only on "ObjectID"s, we don't go deeper into this Object
      // other "objects" will be handled below
      if (args._bsontype && args._bsontype === 'ObjectID') {
        return args;
      }
  }

  // on array like types, return the interpreted mapped values.
  // array elements can be also query objects,
  // so we have to analyze each one of them first, before we return them
  // back as an array
  if (isArrayLike(args) && args.length && args.length > 0) {
    return args.map(item => buildFilterQuery(item));
  }

  // on object like types, analyze the graphql provided query arguments...
  if (isObject(args)) {
    // get all the keys of the argument
    const keys = Object.keys(args);

    // analyze all arguments and map them to mongodb query parts
    keys.forEach(key => {
      // a key might look like: e.g. "bodyText_not_starts_with"
      // to analyze it, we need to split off the field name from the operation
      const keyParts = key.split('_');

      // number of keyParts e.g. 4 as a cloned field, as keyParts changes later
      const length = 0 + keyParts.length;

      // the fieldName is then e.g. "bodyText", get the first array element
      let fieldName = keyParts.shift();

      // our "id" field is in mongo "_id"
      if (fieldName === 'id') {
        fieldName = '_id';
      }

      // the operation is then: e.g. "not_starts_with"
      const operation = keyParts.join('_');

      // the value which was passed in to this operation, might be a deep Object
      // so it has to be analyzed recursively
      const value = args[key];

      switch (length) {
        // if there is only 1 key part provided, it must be one of the special
        // cases such as AND, NOT NOR, OR,
        // or the default case, that it had just provided a simple field name
        case 1:
          switch (fieldName) {
            case 'AND':
              // $and performs a logical AND operation on an array of two or
              // more expressions, (e.g. <expression1>, <expression2>, etc.)
              // and selects the documents, that satisfy all the expressions
              // in the array.
              //     $and   : [ { tags: "ssl" }, { tags: "security" } ]
              query['$and'] = buildFilterQuery(value);
              break;

            case 'NOR':
              // $nor performs a logical NOR operation on an array of one or
              // more query expression and selects the documents that fail
              // all the query expressions in the array.
              // db.inventory.find({ $nor: [ { price: 1.99 }, { sale: true } ]})
              query['$nor'] = buildFilterQuery(value);
              break;

            case 'OR':
              // $or operator performs a logical OR operation on an array of
              // two or more <expressions> and selects the documents that
              // satisfy at least one of the <expressions>.
              // db.inventory.find({
              //   $or: [ { quantity: { $lt: 20 } }, { price: 10 } ]
              // })
              query['$or'] = buildFilterQuery(value);
              break;

            default:
              // the trivial case
              // fieldName = value
              query[fieldName] = buildFilterQuery(value);
              break;
          }
          break;

        // if there are more than one keyParts, we know, it must be
        // one of the following operations,
        // provided from the graphql schema as filter arguments
        default:
          switch (operation) {
            case 'all':
              // $all operator selects the documents, where the value of
              // a field is an array
              // that contains all the specified elements. To specify an
              // $all expression
              //         tags:   {  $all : [ "ssl" , "security"       ] }
              //        field:   {  $all : [ <value1> , <value2> ...  ] }
              query[fieldName] = { $all: buildFilterQuery(value) };
              break;

            case 'eq':
              // $eq specifies equality condition. The $eq operator matches
              // documents where the value of a field equals the specified value
              // db.inventory.find( { qty: { $eq: 20 } } )
              query[fieldName] = { $eq: buildFilterQuery(value) };
              break;

            case 'ne':
              // $ne selects the documents where the value of the field is
              // not equal (i.e. !=) to the specified value. This includes
              // documents that do not contain the field.
              //        field:   {  $ne : value                        }
              query[fieldName] = { $ne: buildFilterQuery(value) };
              break;

            case 'in':
              // $in operator selects the documents where the value of a
              // field equals any value
              // in the specified array.
              // db.inventory.find( { qty: { $in: [ 5, 15 ] } } )
              query[fieldName] = { $in: buildFilterQuery(value) };
              break;

            case 'nin':
              // $nin selects the documents where:
              //  * the field value is not in the specified array or
              //  * the field does not exist.
              // db.inventory.find( { qty: { $nin: [ 5, 15 ] } } )
              query[fieldName] = { $nin: buildFilterQuery(value) };
              break;

            case 'lt':
              // $lt selects the documents where the value of the field is less
              // than (i.e. <) the specified value.
              // db.inventory.find( { qty: { $lt: 20 } } )
              query[fieldName] = { $lt: buildFilterQuery(value) };
              break;

            case 'lte':
              // $lte selects the documents where the value of the field
              // is less than or equal to (i.e. <=) the specified value.
              // db.inventory.find( { qty: { $lte: 20 } } )
              query[fieldName] = { $lte: buildFilterQuery(value) };
              break;

            case 'gt':
              // $gt selects those documents where the value of the field
              // is greater than (i.e. >) the specified value.
              // db.inventory.find( { qty: { $gt: 20 } } )
              query[fieldName] = { $gt: buildFilterQuery(value) };
              break;

            case 'gte':
              // $gte selects the documents where the value of the field
              // is greater than or equal to (i.e. >=) a specified value
              // e.g. value.
              // db.inventory.find( { qty: { $gte: 20 } } )
              query[fieldName] = { $gte: buildFilterQuery(value) };
              break;

            case 'not':
              // $not performs a logical NOT operation on the specified
              //  <operator-expression> and selects the documents that
              // do not match the <operator-expression>.
              // This includes documents that do not contain the field.
              // db.inventory.find( { price: { $not: { $gt: 1.99 } } } )

              const subQuery = buildFilterQuery(value);
              if (subQuery[fieldName]) {
                query[fieldName] = { $not: subQuery[fieldName] };
              } else {
                // this shouldn't happen, but for safety reasons
                query[fieldName] = { $not: buildFilterQuery(value) };
              }
              break;

            case 'exists':
              // Syntax: { field: { $exists: <boolean> } }
              // When <boolean> is true, $exists matches the documents
              // that contain the field, including documents where the
              // field value is null.
              // If <boolean> is false, the query returns only the documents
              //  that do not contain the field.
              query[fieldName] = { $exists: buildFilterQuery(value) };
              break;

            case 'type':
              // $type selects the documents where the value of the field
              // is an instance of the specified BSON type.
              // Querying by data type is useful when dealing with
              // highly unstructured data where data types are not predictable
              // { field: { $type: <BSON type number> | <String alias> } }
              // look for the type: enum BSONType
              query[fieldName] = { $type: buildFilterQuery(value) };
              break;

            case 'not_in':
              // $in operator selects the documents where the value of a
              // field equals any value
              // in the specified array. $not inverses
              // db.inventory.find( { qty: { $in: [ 5, 15 ] } } )
              query[fieldName] = { $nin: buildFilterQuery(value) };
              break;

            case 'regex':
              // in schema defined graphql type:
              // type regex {
              //   regex: String
              //   option: Enum (type option { global, multiline, ...})
              // }
              // so we need to map the options first to mongoDB regex options:
              let options = '';
              if (value.options) {
                value.options.forEach(option => {
                  switch (option) {
                    case 'global':
                      options += 'g';
                      break;
                    case 'multiline':
                      options += 'm';
                      break;
                    case 'insensitive':
                      options += 'i';
                      break;
                    case 'sticky':
                      options += 'y';
                      break;
                    case 'unicode':
                      options += 'u';
                      break;
                  }
                });
              }

              // assign it directly, as deeper structures aren't allowed here
              query[fieldName] = {
                $regex: value.regex,
                $options: options
              };
              break;

            case 'contains':
              // the field contains this string
              query[fieldName] = {
                $regex: buildFilterQuery(value)
              };
              break;

            case 'starts_with':
              // the field starts with this string
              query[fieldName] = {
                $regex: `^${buildFilterQuery(value)}`,
                $options: 'm'
              };
              break;

            case 'ends_with':
              // the field ends with this string
              query[fieldName] = {
                $regex: `${buildFilterQuery(value)}$`,
                $options: 'm'
              };
              break;

            case 'not_contains':
              // the field does not contain this string
              query[fieldName] = {
                $regex: `^(?!.*${buildFilterQuery(value)})`,
                $options: 'm'
              };
              break;

            case 'not_starts_with':
              // the field ends not with this string
              query[fieldName] = {
                $regex: `^(?!${buildFilterQuery(value)})`,
                $options: 'm'
              };
              break;

            case 'not_ends_with':
              // // the field ends with this string
              // query[fieldName] = {
              //   $regex: `(?!${buildFilterQuery(value)})$`,
              //   $options: 'm'
              // };
              // the field ends with this string case-in-sensitive
              // it is called "negated or negative lookbehind" .*(?<!xyz)$
              // Javascript isn't supporting this today
              // so have to use a complicated look ahead pattern instead
              query[fieldName] = {
                $regex: `^(?!.*${buildFilterQuery(value)}$).*$`,
                $options: 'gm'
              };
              break;

            case 'contains_ci':
              // the field contains this string case-in-sensitive
              query[fieldName] = {
                $regex: buildFilterQuery(value),
                $options: 'im'
              };
              break;

            case 'starts_with_ci':
              // the field starts with this string case-in-sensitive
              query[fieldName] = {
                $regex: `^${buildFilterQuery(value)}`,
                $options: 'im'
              };
              break;

            case 'ends_with_ci':
              // the field ends with this string case-in-sensitive
              query[fieldName] = {
                $regex: `${buildFilterQuery(value)}$`,
                $options: 'im'
              };
              break;

            case 'not_contains_ci':
              // the field does not contain this string case-in-sensitive
              query[fieldName] = {
                $regex: `^(?!.*${buildFilterQuery(value)})`,
                $options: 'im'
              };
              break;

            case 'not_starts_with_ci':
              // the field starts with this string case-in-sensitive
              query[fieldName] = {
                // $not: {
                $regex: `^(?!${buildFilterQuery(value)})`,
                $options: 'im'
                // }
              };
              break;

            case 'not_ends_with_ci':
              // the field ends with this string case-in-sensitive
              // it is called "negated or negative lookbehind" .*(?<!xyz)$
              // Javascript isn't supporting this today
              // so have to use a complicated look ahead pattern instead
              query[fieldName] = {
                $regex: `^(?!.*${buildFilterQuery(value)}$).*$`,
                $options: 'gim'
              };
              break;
          }
      }
    });
  }

  return query;
}

// @flow
import { DEFAULT_LIMIT } from '../constants';

/*
 * sets the limit of the cursor: .limit(number)
 * @public
 * @param {object} args - query arguments for the .find() method
 * @return {number} limit - number of records to return
 */

export function buildLimit(args: any): number {
  if (args.limit) {
    return args.limit;
  }

  return DEFAULT_LIMIT;
}

// @flow
import { DEFAULT_SKIP } from '../constants';

/*
 * sets the skip for the cursor:  .skip(number)
 * @public
 * @param {object} args - query arguments for the .find() method
 * @return {number} skip - number of records to skip
 */

export function buildSkip(args: any): number {
  if (args.skip) {
    return args.skip;
  }

  return DEFAULT_SKIP;
}

// @flow
import { DEFAULT_SORT, DESC } from '../constants';

/**
 * sets the sort query for .sort(keyOrList, direction)
 * @public
 * @param {object} args - query arguments for the .find() method
 * @return {object} sortQuery - sort criteria
 */

export function buildSortQuery(args: any): any {
  // keyOrList string | array | object the key or keys for the sort
  const sortQuery = {};

  // if there is no sort criteria, use at least the order of creation time
  // which we've stored in the constants as defaults
  if (!args.orderBy || args.orderBy.length === 0) {
    return DEFAULT_SORT;
  }

  // if there are sort criterias from the schema's "enum <type>Sort"
  // sort = sort field name
  // direction = number ASC => 1, DESC => -1
  // mongoDB sort directions for ASC = 1, for DESC = -1
  // if there is no direction provided set default to ASC
  args.orderBy.forEach(
    ({ sort, direction }) => (sortQuery[sort] = direction === DESC ? -1 : 1)
  );

  return sortQuery;
}

// @flow
/**
 * @module prepareQueries
 * @default
 * prepares all queries and returns the query objects
 */

export { prepareQueries } from './prepareQueries';

// @flow
import { buildBaseQuery } from './buildBaseQuery';
import { buildSortQuery } from './buildSortQuery';
import { buildSkip } from './buildSkip';
import { buildLimit } from './buildLimit';

/*
 * prepares the queries for the mongoDB access
 * @public
 * @param {object} args - query arguments for the mongoDB db operations
 * @return {object} queries - all necessary queries
 * @properties {object} baseQuery - base query for the .find() method
 * @properties {object} sortQuery - sort query for the .sort() method
 * @properties {number} skip - number of records to skip .skip() method
 * @properties {number} limit - number of records to return .limit() method
 */

export function prepareQueries(
  args: any
): {
  baseQuery: any,
  sortQuery: any,
  skip: number,
  limit: number
} {
  return {
    baseQuery: buildBaseQuery(args),
    sortQuery: buildSortQuery(args),
    skip: buildSkip(args),
    limit: buildLimit(args)
  };
}

// import { readString } from '../util/read';
// import path from 'path';

import { requiredTypes } from './requiredTypes';

/**
 * reads and graphql file and returns it as a string
 * @return {string} requiredTypes - schema containing the required types
 */

export function buildRequiredTypes(): string {
  // for any reason npm install always forgets to install the .graphql file
  // const filePath = path.join(__dirname, pathToFile || 'requiredTypes.conf');
  // return readString(filePath);
  return requiredTypes;
}

// @flow
import cloneDeep from 'lodash.clonedeep';

import {
  getBaseType,
  buildTypeDefinition,
  buildField,
  buildValue,
  buildArgument,
  isScalarField
} from '../util/graphql';

import {
  DOCUMENT,
  OBJECT_TYPE_DEFINITION,
  INPUT_OBJECT_TYPE_DEFINITION,
  ENUM_TYPE_DEFINITION,
  TYPE_EXTENSION_DEFINITION,
  LIST_TYPE,
  FIELD_DEFINITION,
  NAME
} from 'graphql/language/kinds';

import {
  INT,
  FLOAT,
  STRING,
  ENUM,
  BOOLEAN,
  BSON_TYPE,
  REGEX,
  QUERY
} from '../constants';

import { lcFirst } from '../util/capitalization';

/**
 * prepares the schema for the additional query arguments types
 * @public
 * @param {object} inputSchema - the input's schema with all fields
 * @return {object} enhancedSchema - the enhanced output Schema
 */

export function enhanceSchemaWithQueryArguments(inputSchema: any): any {
  const enhancedSchema = cloneDeep(inputSchema);
  const queryArguments = {};

  if (enhancedSchema.kind === DOCUMENT) {
    enhancedSchema.definitions
      .filter(def => def.kind === OBJECT_TYPE_DEFINITION)
      .forEach(({ fields, name }) => {
        const TypeName = name.value;

        // get all field definition types
        const fieldTypes = getFieldTypes(fields);

        // prepare "filter" query argument fields for scalar types
        const filterInputFields = getFilterInputFields(fieldTypes, TypeName);

        // prepare an enum to contain all sortable fields for the orderBy
        const sortEnumValues = getEnumValues(fieldTypes);

        // prepare "orderBy" query argument fields
        const orderByInputFields = getOrderByInputFields(TypeName);

        if (filterInputFields.length > 0) {
          const queryFieldName = `${lcFirst(TypeName)}s`;
          const FilterInputTypeName = `${TypeName}Filter`;
          const SortEnumTypeName = `${TypeName}Sort`;
          const OrderByInputTypeName = `${TypeName}OrderBy`;

          addFilterInputType(
            FilterInputTypeName,
            filterInputFields,
            enhancedSchema
          );
          addSortEnumType(SortEnumTypeName, sortEnumValues, enhancedSchema);
          addOrderByInputType(
            OrderByInputTypeName,
            orderByInputFields,
            enhancedSchema
          );

          queryArguments[queryFieldName] = {
            filterName: FilterInputTypeName,
            orderByName: `[${OrderByInputTypeName}!]`
          };
        }
      });

    // add the newly created types to the Query itself,
    // to make them accessable by users
    addQueryArguments(enhancedSchema, queryArguments);
  }

  return enhancedSchema;
}

/**
 * prepares the orderBy input type
 * @private
 * @param {string} TypeName - the name of the type
 * @return {object} fieldAST - the AST of an input type field
 */

function getOrderByInputFields(TypeName) {
  // prepare "orderBy" query argument fields
  return [
    buildField('sort', [], `${TypeName}Sort!`),
    buildField('direction', [], 'Direction = ASC')
  ];
}

/**
 * prepare enum values out of field types
 * @private
 * @param {array} fieldTypes - AST array of field definitions
 * @return {object} fieldAST - the AST of enum values
 */

function getEnumValues(fieldTypes) {
  // prepare an enum to contain all sortable fields for the orderBy
  return fieldTypes
    .filter(field => field.scalar)
    .map(field => buildValue(field.name, [], STRING));
}

/**
 * prepare enum values out of field types
 * @private
 * @param {array} fieldTypes - AST array of field definitions
 * @param {string} TypeName - the name of the type
 * @return {object} fieldAST - the AST of enum values
 */

function getFilterInputFields(fieldTypes, TypeName) {
  // prepare "filter" query argument fields for scalar types
  return fieldTypes
    .filter(field => field.scalar)
    .map(field => prepareArgument(field, TypeName))
    .reduce((array, field) => array.concat(field), [
      { [`AND`]: `[${TypeName}Filter!]` },
      { [`NOR`]: `[${TypeName}Filter!]` },
      { [`OR`]: `[${TypeName}Filter!]` }
    ])
    .map(field => {
      const name = Object.keys(field)[0];
      const type = field[name];
      return buildField(name, [], type);
    });
}

/**
 * analyse and identify field information for further processing
 * @private
 * @param {array} fields - AST array of field definitions
 * @return {array} fieldAST - list of field descriptions
 */

function getFieldTypes(fields) {
  return fields.map(field => ({
    name: field.name.value,
    type: getBaseType(field.type).name.value,
    scalar: isScalarField(field)
  }));
}

/**
 * registers the "filter" input type into the AST
 * @private
 * @param {string} FilterInputTypeName - name of the input field
 * @param {array} filterInputFields - AST array of field definitions
 * @param {object} enhancedSchema - AST of the document
 */

function addFilterInputType(
  FilterInputTypeName,
  filterInputFields,
  enhancedSchema
) {
  // add "input <TypeName>Filter" type to the schema
  enhancedSchema.definitions.push(
    buildTypeDefinition(
      FilterInputTypeName,
      filterInputFields,
      INPUT_OBJECT_TYPE_DEFINITION
    )
  );
}

/**
 * registers the "enum <Type>Sort" enum type into the AST
 * @private
 * @param {string} SortEnumTypeName - name of the enum field
 * @param {array} sortEnumValues - AST array of field definitions
 * @param {object} enhancedSchema - AST of the document
 */

function addSortEnumType(SortEnumTypeName, sortEnumValues, enhancedSchema) {
  // add "enum <TypeName>Sort" type
  enhancedSchema.definitions.push(
    buildTypeDefinition(
      SortEnumTypeName,
      [],
      ENUM_TYPE_DEFINITION,
      sortEnumValues
    )
  );
}

/**
 * registers the "input <Type>OrderBy" input type into the AST
 * @private
 * @param {string} OrderByInputTypeName - name of the input field
 * @param {array} orderByInputFields - AST array of field definitions
 * @param {object} enhancedSchema - AST of the document
 */

function addOrderByInputType(
  OrderByInputTypeName,
  orderByInputFields,
  enhancedSchema
) {
  // add "input <TypeName>OrderBy" type
  enhancedSchema.definitions.push(
    buildTypeDefinition(
      OrderByInputTypeName,
      orderByInputFields,
      INPUT_OBJECT_TYPE_DEFINITION
    )
  );
}

/**
 * adds the query arguments "filter" and "orderBy" to the "extend Query" type
 * @private
 * @param {object} enhancedSchema - AST document definition, to be enhanced
 * @param {array} queryArguments - dictionary of queryArguments to be added
 */

function addQueryArguments(enhancedSchema, queryArguments) {
  // add the arguments to the Query:
  // e.g.: tweets(filter: TweetFilter, orderBy: [TweetOrderBy!])
  enhancedSchema.definitions
    .filter(
      def =>
        def.kind === TYPE_EXTENSION_DEFINITION &&
        def.definition.kind === OBJECT_TYPE_DEFINITION &&
        def.definition.name.value === QUERY
    )
    .forEach(({ definition }) => {
      const { fields, name } = definition;

      if (fields && name) {
        // find the right fields
        fields
          // only in field definition, which are a list, such as 'users'
          .filter(
            field =>
              field.type.kind === LIST_TYPE && field.kind === FIELD_DEFINITION
          )
          .forEach(field => {
            // as arguments is a reserved field, we need to rename to args
            const args = field.arguments;

            const queryArgument = queryArguments[field.name.value];
            // if we have arguments to add for this type e.g. 'users'
            if (queryArgument && field.name.kind === NAME && args) {
              // add "skip" query argument
              args.push(buildArgument('skip', 'Int'));

              // add "filter" query argument
              args.push(buildArgument('filter', queryArgument.filterName));

              // add "orderBy" query argument
              args.push(buildArgument('orderBy', queryArgument.orderByName));
            }
          });
      }
    });
}

/**
 * define the combinations of input and output types
 * depending on the later expected values for the functions
 * @private
 * @param {object} field - field of the type
 * @property {string} name - name of the field
 * @property {string} type - type of the field
 * @param {string} TypeName - name of the type
 * @return {array} argumentFields - map of arguments and their types
 */

function prepareArgument({ name, type }, TypeName) {
  switch (type) {
    case STRING:
    case ENUM:
      return [
        { [`${name}`]: type },
        { [`${name}_lt`]: type },
        { [`${name}_lte`]: type },
        { [`${name}_gt`]: type },
        { [`${name}_gte`]: type },
        { [`${name}_eq`]: type },
        { [`${name}_ne`]: type },
        { [`${name}_not`]: `${TypeName}Filter` },
        { [`${name}_all`]: `[${type}!]` },
        { [`${name}_in`]: `[${type}!]` },
        { [`${name}_nin`]: `[${type}!]` },
        { [`${name}_not_in`]: `[${type}!]` },
        { [`${name}_exists`]: BOOLEAN },
        { [`${name}_type`]: BSON_TYPE },
        { [`${name}_regex`]: REGEX },
        { [`${name}_contains`]: STRING },
        { [`${name}_starts_with`]: STRING },
        { [`${name}_ends_with`]: STRING },
        { [`${name}_not_contains`]: STRING },
        { [`${name}_not_starts_with`]: STRING },
        { [`${name}_not_ends_with`]: STRING },
        { [`${name}_contains_ci`]: STRING },
        { [`${name}_starts_with_ci`]: STRING },
        { [`${name}_ends_with_ci`]: STRING },
        { [`${name}_not_contains_ci`]: STRING },
        { [`${name}_not_starts_with_ci`]: STRING },
        { [`${name}_not_ends_with_ci`]: STRING }
      ];

    case INT:
    case FLOAT:
      return [
        { [`${name}`]: type },
        { [`${name}_lt`]: type },
        { [`${name}_lte`]: type },
        { [`${name}_gt`]: type },
        { [`${name}_gte`]: type },
        { [`${name}_eq`]: type },
        { [`${name}_ne`]: type },
        { [`${name}_all`]: `[${type}!]` },
        { [`${name}_in`]: `[${type}!]` },
        { [`${name}_nin`]: `[${type}!]` },
        { [`${name}_not_in`]: `[${type}!]` },
        { [`${name}_exists`]: BOOLEAN },
        { [`${name}_type`]: BSON_TYPE }
      ];

    case BOOLEAN:
    default:
      return [
        { [`${name}`]: type },
        { [`${name}_eq`]: type },
        { [`${name}_ne`]: type },
        { [`${name}_all`]: `[${type}!]` },
        { [`${name}_in`]: `[${type}!]` },
        { [`${name}_nin`]: `[${type}!]` },
        { [`${name}_not_in`]: `[${type}!]` },
        { [`${name}_exists`]: BOOLEAN },
        { [`${name}_type`]: BSON_TYPE }
      ];
  }
}

